#include "RUnionSummary.h"



RUnionSummary::RUnionSummary() : SendPacket(C_UNION_SUMMARY)
{
}

void RUnionSummary::Process(OpCode opCode, Stream * data, Client * caller)const
{
	data->Clear();

	data->WriteInt16(0);
	data->WriteInt16(S_UNION_SUMMARY);
	data->WriteHexString("");
	data->WritePos(0);

	caller->Send(data);
}